CREATE FUNCTION fc_entry(vpos integer, vdata character varying, vsepar character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$

declare  vVALUE    varchar(8000);   
         i    integer; 
         j    integer;  
         vWORD varchar(8000);  
  
begin  
  vVALUE = '';  
  if (vPOS < 1 or vDATA = '') then 
    return '';
  end if;  
  
   
  vWORD = '';  
  i = 1;  
  j = 0;  
    
  loop exit when i > vPOS ;
    if strpos(vDATA, vSEPAR) > 0 then
      vWORD = substring(vDATA,1, strpos(vDATA, vSEPAR) -1);
      vDATA = substring(vDATA, strpos(vDATA, vSEPAR)+1, 
                            length(vDATA) -  strpos(vDATA, vSEPAR));
      j = i;
    else
      vWORD = vDATA;
      j = i;
      exit;
    end if;
    i = i + 1;
  end loop;
  
  if j = vPOS then
    vVALUE = vWORD;
  end if;
  
  return vVALUE;  
  
end;  

$$;


