CREATE FUNCTION fc_lookup(vsearch character varying, vdata character varying, vsepar character varying) RETURNS integer
    LANGUAGE plpgsql
    AS $$

declare vPOS integer; 

vCOUNTER  integer;
vNUM_WORDS integer;
begin

        
vCOUNTER = 1;
vNUM_WORDS = fc_num_entry(vDATA, vSEPAR);

loop exit when vCOUNTER > vNUM_WORDS;
  if fc_entry(vCOUNTER, vDATA, vSEPAR) = vSEARCH then
    vPOS = vCOUNTER;
    exit;
  end if;
  vCOUNTER = vCOUNTER + 1; 
end loop;

return vPOS;

end;

$$;


