-- Name: fc_num_entry(character varying, character varying); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION fc_num_entry(vdata character varying, vsepar character varying) RETURNS integer
    LANGUAGE plpgsql
    AS $$
   

declare vNUMBER integer;  
    vi integer;   
         vj integer;   
         vk integer ; 
  
begin  
  vNUMBER = 0;    
  if (length(vDATA) = 0 or vDATA = '') then
    return 0;  
  end if;

    
  vSEPAR = vSEPAR;  
  vk     = 0;
  vj     = 0;  
  vi     = strpos(vDATA, vSEPAR);  

  loop exit when vi <= 0;  
  vi = strpos(vDATA, vSEPAR);
  if vi > 0 then
    vDATA = substring(vDATA, vi+1, length(vDATA) - vi);
    vk = vk + 1;
 end if;                
end loop;
    
vNUMBER = vk + 1;  
  
return vNUMBER;  
  
  
end;

$$;


